// src/pages/ProductsDetails.jsx  (أو مكان الملف عندك، نفس الاسم اللي استوردته في App)
import React, { useEffect, useState } from "react";
import {
  Container,
  Row,
  Col,
  Image,
  Button,
  Spinner,
  Form,
  Card,
} from "react-bootstrap";
import { useNavigate, useLocation, useParams } from "react-router-dom";
import ProductDetailsHeader from "../components/ProductDetailsHeader";

const API_BASE_URL = "http://localhost:8000/api";

const getFetchOptions = (additional = {}) => ({
  credentials: "include",
  ...additional,
});

const checkAuth = () => {
  const user = JSON.parse(localStorage.getItem("user") || "{}");
  return user.id ? true : false;
};

export default function ProductDetails() {
  const navigate = useNavigate();
  const location = useLocation();
  const { id: idParam } = useParams(); // <-- read path param
  const [product, setProduct] = useState(null);
  const [loading, setLoading] = useState(true);
  const [quantity, setQuantity] = useState(1);
  const [favorites, setFavorites] = useState([]);
  const [cart, setCart] = useState([]);

  // Helper: read id from query string if no param
  const getIdFromQuery = () => {
    const params = new URLSearchParams(location.search);
    return params.get("id");
  };

  // final id to use (either param or query)
  const idFromQuery = getIdFromQuery();
  const id = idParam || idFromQuery;

  useEffect(() => {
    // Redirect to login if not authenticated
    if (!checkAuth()) {
      navigate("/login");
      return;
    }

    // If there's no id at all, go to products
    if (!id) {
      navigate("/products");
      return;
    }

    // If id came from query (old links), replace URL to canonical path
    if (!idParam && idFromQuery) {
      navigate(`/product-details/${id}`, { replace: true });
      return; // wait for navigation (component may re-render with param)
    }

    // load product only when we have the path param or after redirect
    loadProductDetails();

    // We intentionally depend on idParam and location.search indirectly via id.
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [idParam, location.search]);

  const loadUserData = async () => {
    try {
      const favRes = await fetch(`${API_BASE_URL}/favorites`, getFetchOptions());
      if (favRes.ok) {
        const data = await favRes.json();
        setFavorites(data.data.favorites.map((f) => f.productId));
      }
      const cartRes = await fetch(`${API_BASE_URL}/cart`, getFetchOptions());
      if (cartRes.ok) {
        const data = await cartRes.json();
        setCart(data.data.cartItems.map((i) => i.productId));
      }
    } catch (err) {
      console.warn("Failed to load user data", err);
    }
  };

  const loadProductDetails = async () => {
    // use the path param if available (after possible redirect)
    const productId = idParam || getIdFromQuery();
    if (!productId) {
      navigate("/products");
      return;
    }

    setLoading(true);
    try {
      await loadUserData();
      const res = await fetch(`${API_BASE_URL}/products/${productId}`);
      if (!res.ok) throw new Error("Product not found");
      const data = await res.json();
      setProduct(data.data.product);
      setQuantity(1);
    } catch (err) {
      console.error(err);
      navigate("/products");
    } finally {
      setLoading(false);
    }
  };

  const changeQuantity = (delta) => {
    if (!product) return;
    const newQ = quantity + delta;
    if (newQ >= 1 && newQ <= product.stock) setQuantity(newQ);
  };

  const onSetQuantity = (val) => {
    if (!product) return;
    const n = parseInt(val, 10) || 1;
    if (n >= 1 && n <= product.stock) setQuantity(n);
  };

  const addToCart = async () => {
    if (!product) return;
    try {
      const res = await fetch(
        `${API_BASE_URL}/cart`,
        getFetchOptions({
          method: "POST",
          headers: { "Content-Type": "application/json" },
          body: JSON.stringify({ productId: product.id, quantity }),
        })
      );
      if (!res.ok) throw new Error("Add failed");
      setCart((p) => [...p, product.id]);
      alert(`Added ${quantity} ${product.name}(s) to cart!`);
    } catch (err) {
      alert("Failed to add to cart");
    }
  };

  const removeFromCart = async () => {
    if (!product) return;
    try {
      const res = await fetch(
        `${API_BASE_URL}/cart/${product.id}`,
        getFetchOptions({ method: "DELETE" })
      );
      if (!res.ok) throw new Error("Remove failed");
      setCart((p) => p.filter((id) => id !== product.id));
      alert("Removed from cart!");
    } catch (err) {
      alert("Failed to remove from cart");
    }
  };

  const toggleFavorite = async () => {
    if (!product) return;
    const isFav = favorites.includes(product.id);
    try {
      if (isFav) {
        const res = await fetch(
          `${API_BASE_URL}/favorites/${product.id}`,
          getFetchOptions({ method: "DELETE" })
        );
        if (!res.ok) throw new Error("Failed");
        setFavorites((f) => f.filter((id) => id !== product.id));
      } else {
        const res = await fetch(
          `${API_BASE_URL}/favorites`,
          getFetchOptions({
            method: "POST",
            headers: { "Content-Type": "application/json" },
            body: JSON.stringify({ productId: product.id }),
          })
        );
        if (!res.ok) throw new Error("Failed");
        setFavorites((f) => [...f, product.id]);
      }
    } catch (err) {
      alert("Network error. Please try again.");
    }
  };

  const logout = async () => {
    try {
      await fetch(`${API_BASE_URL}/auth/logout`, {
        method: "POST",
        credentials: "include",
      });
      localStorage.removeItem("user");
      navigate("/");
    } catch {
      alert("Logout failed");
    }
  };

  if (loading)
    return (
      <>
        <ProductDetailsHeader onLogout={logout} />
        <Container className="my-5 text-white text-center">
          <Spinner animation="border" />
        </Container>
      </>
    );

  if (!product) return null;

  const inCart = cart.includes(product.id);
  const isFavorite = favorites.includes(product.id);
  const isOutOfStock = product.stock <= 0;

  return (
    <>
      <ProductDetailsHeader onLogout={logout} />
      <Container className="my-5 text-white">
        <Card className="product-details-card">
          <Card.Body>
            <Row>
              <Col lg={6}>
                <Image
                  src={
                    product.image
                      ? `http://localhost:8000/uploads/products/${product.image}`
                      : "https://via.placeholder.com/600x400"
                  }
                  alt={product.name}
                  fluid
                  className="product-image"
                />
              </Col>

              <Col lg={6}>
                <h1 className="product-title">{product.name}</h1>
                <div className="product-category">{product.category}</div>
                <div className="product-price">${product.price}</div>
                <div
                  className={`product-stock ${isOutOfStock ? "out-of-stock" : ""}`}
                >
                  {isOutOfStock ? "Out of Stock" : `${product.stock} items in stock`}
                </div>
                <p className="product-description">{product.description}</p>

                {!isOutOfStock && (
                  <div className="quantity-selector mb-3">
                    <label>Quantity:</label>
                    <div className="d-flex align-items-center mt-2">
                      <Button
                        variant="primary"
                        onClick={() => changeQuantity(-1)}
                        disabled={quantity <= 1}
                      >
                        -
                      </Button>
                      <Form.Control
                        type="number"
                        value={quantity}
                        onChange={(e) => onSetQuantity(e.target.value)}
                        className="mx-2 quantity-input"
                        style={{ width: "100px" }}
                        min={1}
                        max={product.stock}
                      />
                      <Button
                        variant="primary"
                        onClick={() => changeQuantity(1)}
                        disabled={quantity >= product.stock}
                      >
                        +
                      </Button>
                    </div>
                  </div>
                )}

                <div className="product-actions">
                  {!isOutOfStock && !inCart && (
                    <Button variant="success" className="me-2" onClick={addToCart}>
                      Add to Cart
                    </Button>
                  )}
                  {inCart && (
                    <Button variant="danger" className="me-2" onClick={removeFromCart}>
                      Remove from Cart
                    </Button>
                  )}
                  {isOutOfStock && (
                    <Button variant="secondary" disabled>
                      Out of Stock
                    </Button>
                  )}
                  <Button
                    variant={isFavorite ? "outline-danger" : "outline-primary"}
                    onClick={toggleFavorite}
                  >
                    {isFavorite ? "Remove from Favorites" : "Add to Favorites"}
                  </Button>
                </div>
              </Col>
            </Row>
          </Card.Body>
        </Card>
      </Container>
    </>
  );
}
