import React, { useState } from 'react';
import { Form, Button, Card, Container, Alert } from 'react-bootstrap';
import { useNavigate } from 'react-router-dom';
import '../styles/auth.css';

const ForgotPassword = () => {
  const [email, setEmail] = useState('');
  const [alert, setAlert] = useState({ message: '', type: '' });
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();

  const API_BASE_URL = 'http://localhost:8000/api';

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setAlert({ message: '', type: '' });

    try {
      const response = await fetch(`${API_BASE_URL}/auth/forgot-password`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ email }),
      });

      const data = await response.json();

      if (response.ok) {
        setAlert({
          message: 'Password reset link sent to your email! Please check your inbox.',
          type: 'success',
        });
        setEmail('');
      } else {
        setAlert({
          message: data.message || 'Failed to send reset link. Please try again.',
          type: 'danger',
        });
      }
    } catch (error) {
      setAlert({ message: 'Network error. Please try again.', type: 'danger' });
    } finally {
      setLoading(false);
    }
  };

  // Optional: redirect if user already logged in
  React.useEffect(() => {
    const user = localStorage.getItem('user');
    if (user) navigate('/products');
  }, [navigate]);

  return (
    <div className="auth-container">
      <Card className="auth-card">
        <div className="text-center mb-4">
          <h2>Forgot Password</h2>
          <p>Enter your email address and we'll send you a link to reset your password.</p>
        </div>

        {alert.message && <Alert variant={alert.type}>{alert.message}</Alert>}

        <Form onSubmit={handleSubmit}>
          <Form.Group className="mb-3" controlId="email">
            <Form.Label className="form-label">Email Address</Form.Label>
            <Form.Control
              type="email"
              className="form-control"
              value={email}
              required
              onChange={(e) => setEmail(e.target.value)}
            />
          </Form.Group>

          <Button type="submit" className="btn-primary w-100 mb-3" disabled={loading}>
            {loading ? 'Sending...' : 'Send Reset Link'}
          </Button>
        </Form>

        <div className="text-center">
          <p>
            Remember your password?{' '}
            <a href="/login" className="text-decoration-none">
              Login here
            </a>
          </p>
        </div>
      </Card>
    </div>
  );
};

export default ForgotPassword;
