import React, { useState } from 'react';
import { Form, Button, Container, Card } from 'react-bootstrap';
import { useNavigate } from 'react-router-dom';
import { auth, GoogleAuthProvider, FacebookAuthProvider } from '../firebase/config';
import { signInWithPopup } from 'firebase/auth';
import '../styles/l';

const API_BASE_URL = 'http://localhost:8000/api';

const Login = () => {
  const navigate = useNavigate();
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');

  const showMessage = (msg, type = 'danger') => {
    alert(msg); // مؤقتاً، يمكن لاحقاً تعمل Toast
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const response = await fetch(`${API_BASE_URL}/auth/login`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        credentials: 'include',
        body: JSON.stringify({ email, password }),
      });

      const data = await response.json();

      if (response.ok) {
        localStorage.setItem('user', JSON.stringify(data.data.user));
        showMessage('Login successful!', 'success');
        setTimeout(() => navigate('/products'), 1000);
      } else {
        showMessage(data.message || 'Login failed');
      }
    } catch {
      showMessage('Network error. Please try again.');
    }
  };

  const socialLogin = async (providerType) => {
    try {
      const provider = providerType === 'google' ? new GoogleAuthProvider() : new FacebookAuthProvider();
      const result = await signInWithPopup(auth, provider);
      const user = result.user;
      const idToken = await user.getIdToken();

      const userData = {
        id: user.uid,
        firstName: user.displayName?.split(' ')[0] || 'User',
        lastName: user.displayName?.split(' ').slice(1).join(' ') || 'Name',
        email: user.email,
        role: 'USER',
        avatar: user.photoURL || 'profile.png',
      };

      const response = await fetch(`${API_BASE_URL}/auth/firebase-login`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        credentials: 'include',
        body: JSON.stringify({ idToken, userData }),
      });

      const data = await response.json();

      if (response.ok) {
        localStorage.setItem('user', JSON.stringify(data.data.user));
        showMessage('Social sign-in successful!', 'success');
        setTimeout(() => navigate('/products'), 1000);
      } else {
        showMessage('Authentication failed');
      }
    } catch {
      showMessage('Social sign-in failed');
    }
  };

  return (
    <div className="auth-container">
      <Card className="auth-card">
        <div className="text-center mb-4">
          <h2>MechObject</h2>
          <p>Welcome back! Please login to your account</p>
        </div>

        <Form onSubmit={handleSubmit}>
          <Form.Group className="mb-3" controlId="email">
            <Form.Label>Email Address</Form.Label>
            <Form.Control type="email" required value={email} onChange={(e) => setEmail(e.target.value)} />
          </Form.Group>

          <Form.Group className="mb-3" controlId="password">
            <Form.Label>Password</Form.Label>
            <Form.Control type="password" required value={password} onChange={(e) => setPassword(e.target.value)} />
          </Form.Group>

          <Button type="submit" className="w-100 mb-3">Login</Button>
        </Form>

        <div className="text-center mb-3">
          <p>Or login with:</p>
          <Button variant="danger" className="me-2 btn-google" onClick={() => socialLogin('google')}>
            Google
          </Button>
          <Button variant="primary" className="btn-facebook" onClick={() => socialLogin('facebook')}>
            Facebook
          </Button>
        </div>

        <div className="text-center">
          <p>Don't have an account? <a href="/register">Register here</a></p>
          <p><a href="/forgot-password">Forgot Password?</a></p>
          <p><a href="/">← Back to Home</a></p>
        </div>
      </Card>
    </div>
  );
};

export default Login;
