import React from 'react';
import { Navbar, Nav, Container } from 'react-bootstrap';
import { Link } from 'react-router-dom';
import '../styles/header.css';

const Header = () => {
  const user = JSON.parse(localStorage.getItem('user') || '{}');
  const isAdmin = user?.role === 'ADMIN';

  const handleLogout = () => {
    localStorage.removeItem('user');
    window.location.reload(); // إعادة تحميل الصفحة لتحديث الهيدر
  };

  return (
    <Navbar expand="lg" className="navbar bg-dark" variant="dark">
      <Container>
        <Navbar.Brand as={Link} to="/">MechObject</Navbar.Brand>
        <Navbar.Toggle />
        <Navbar.Collapse>
          <Nav className="me-auto">
            <Nav.Link as={Link} to="/">Home</Nav.Link>
            <Nav.Link as={Link} to="/products">Products</Nav.Link>
            <Nav.Link as={Link} to="/about">About</Nav.Link>
            <Nav.Link as={Link} to="/contact">Contact</Nav.Link>
          </Nav>
          <Nav>
            {user?.id ? (
              <>
                <Nav.Link as={Link} to="/favorites">Favorites</Nav.Link>
                <Nav.Link as={Link} to="/cart">Cart</Nav.Link>
                {isAdmin && <Nav.Link as={Link} to="/admin">Dashboard</Nav.Link>}
                <Nav.Link onClick={handleLogout}>Logout</Nav.Link>
              </>
            ) : (
              <>
                <Nav.Link as={Link} to="/login">Login</Nav.Link>
                <Nav.Link as={Link} to="/register">Register</Nav.Link>
              </>
            )}
          </Nav>
        </Navbar.Collapse>
      </Container>
    </Navbar>
  );
};

export default Header;
