import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import { Button, Form, Row, Col, Alert } from "react-bootstrap";
import "./auth.css";

// Firebase
import { initializeApp } from "firebase/app";
import {
  getAuth,
  signInWithPopup,
  GoogleAuthProvider,
  FacebookAuthProvider,
} from "firebase/auth";

const firebaseConfig = {
  apiKey: "AIzaSyCy5RH5f5flQFaqMRzv5iVuGyeS8P9AGfs",
  authDomain: "mechobject-7b416.firebaseapp.com",
  projectId: "mechobject-7b416",
  storageBucket: "mechobject-7b416.appspot.com",
  messagingSenderId: "269505965370",
  appId: "1:269505965370:web:d82b51ddeee673d2c87403",
  measurementId: "G-F9LMEXCHZP",
};

const app = initializeApp(firebaseConfig);
const auth = getAuth(app);

const API_BASE_URL = "http://localhost:8000/api";

const Register = () => {
  const navigate = useNavigate();
  const [form, setForm] = useState({
    firstName: "",
    lastName: "",
    email: "",
    password: "",
    confirmPassword: "",
  });
  const [alert, setAlert] = useState(null);

  useEffect(() => {
    const user = localStorage.getItem("user");
    if (user) navigate("/products");
  }, [navigate]);

  const showMessage = (message, type = "danger") => {
    setAlert({ message, type });
    setTimeout(() => setAlert(null), 5000);
  };

  const handleChange = (e) => {
    setForm({ ...form, [e.target.id]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (form.password !== form.confirmPassword) {
      return showMessage("Passwords do not match");
    }
    try {
      const res = await fetch(`${API_BASE_URL}/auth/register`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        credentials: "include",
        body: JSON.stringify({
          firstName: form.firstName,
          lastName: form.lastName,
          email: form.email,
          password: form.password,
        }),
      });
      const data = await res.json();
      if (res.ok) {
        localStorage.setItem("user", JSON.stringify(data.data.user));
        showMessage("Registration successful! Redirecting...", "success");
        setTimeout(() => navigate("/products"), 1000);
      } else {
        showMessage(data.message || "Registration failed");
      }
    } catch (err) {
      showMessage("Network error. Please try again.");
    }
  };

  const handleSocialLogin = async (provider) => {
    try {
      const result = await signInWithPopup(auth, provider);
      const user = result.user;
      const idToken = await user.getIdToken();

      const userData = {
        id: user.uid,
        firstName: user.displayName?.split(" ")[0] || "User",
        lastName: user.displayName?.split(" ").slice(1).join(" ") || "Name",
        email: user.email,
        role: "USER",
        avatar: user.photoURL || "profile.png",
      };

      const response = await fetch(`${API_BASE_URL}/auth/firebase-login`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        credentials: "include",
        body: JSON.stringify({ idToken, userData }),
      });

      if (response.ok) {
        const data = await response.json();
        localStorage.setItem("user", JSON.stringify(data.data.user));
        showMessage("Login successful! Redirecting...", "success");
        setTimeout(() => navigate("/products"), 1000);
      } else {
        showMessage("Social login failed");
      }
    } catch (error) {
      showMessage("Social login failed. Try again.");
    }
  };

  return (
    <div className="auth-container">
      <div className="auth-card">
        <div className="text-center mb-4">
          <h2>MechObject</h2>
          <p>Create your account and start shopping</p>
        </div>

        {alert && <Alert variant={alert.type}>{alert.message}</Alert>}

        <Form onSubmit={handleSubmit}>
          <Row>
            <Col md={6} className="mb-3">
              <Form.Label>First Name</Form.Label>
              <Form.Control id="firstName" value={form.firstName} onChange={handleChange} required />
            </Col>
            <Col md={6} className="mb-3">
              <Form.Label>Last Name</Form.Label>
              <Form.Control id="lastName" value={form.lastName} onChange={handleChange} required />
            </Col>
          </Row>
          <Form.Group className="mb-3">
            <Form.Label>Email Address</Form.Label>
            <Form.Control id="email" type="email" value={form.email} onChange={handleChange} required />
          </Form.Group>
          <Form.Group className="mb-3">
            <Form.Label>Password</Form.Label>
            <Form.Control id="password" type="password" value={form.password} onChange={handleChange} required minLength={6} />
          </Form.Group>
          <Form.Group className="mb-3">
            <Form.Label>Confirm Password</Form.Label>
            <Form.Control id="confirmPassword" type="password" value={form.confirmPassword} onChange={handleChange} required />
          </Form.Group>
          <Button type="submit" className="w-100 mb-3 btn-primary">
            Create Account
          </Button>
        </Form>

        <div className="text-center mb-3">
          <p>Or register with:</p>
          <Button className="btn-google me-2" onClick={() => handleSocialLogin(new GoogleAuthProvider())}>
            Google
          </Button>
          <Button className="btn-facebook" onClick={() => handleSocialLogin(new FacebookAuthProvider())}>
            Facebook
          </Button>
        </div>

        <div className="text-center">
          <p>
            Already have an account? <a href="/login">Login here</a>
          </p>
          <p>
            <a href="/">‚Üê Back to Home</a>
          </p>
        </div>
      </div>
    </div>
  );
};

export default Register;
