const MainLayout = () => {
  const location = useLocation();

  // صفحات بدون فوتر:
  const noFooterPages = ["/products"]; // أضف المسارات اللي ما بدك فيها فوتر

  const hideFooter = noFooterPages.includes(location.pathname);

  return (
    <>
      <Header />
      <Outlet />
      {!hideFooter && <Footer />}
    </>
  );
};
